    @Before
    fun setUp() {
        OoRestManager.init(false)
    }

    @Test
    fun requestGuardian() {
        val signal = CountDownLatch(1)

        val params = OoParamRequestGuardian()
        params.userToken = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..TqR52J-5ODiPdnRLA8t2FxumpyXH8HL28IlRjO8-i8I"
        params.seniorId = "bzT8GybqLYyyRJdXlyJR"

        OoRestManager.requestGuardian(params) { error, response ->
            Assert.assertEquals(null, error)
            Assert.assertEquals(true, response?.isSuccess())
            signal.countDown()
        }
        signal.await()
    }

